  return (
    <>
      <nav>
        <div className="flex">
            <h1>FetchingData</h1>  
            <button onClick={loadUser} class="btn " id="openBtn">Get User</button> :
            <button onClick={closebtn} class="btn-close " id="closeBtn"></button> 
        </div>
      </nav>
    </>
  );
};

export default Nav;
