import React, { useState,useEffect } from "react";
import "./App.css";
import Nav from "./Components/Nav";

const App = () => {
  const [user, setUser] = useState([]);

  const loadUser = async () => {
    const listLayout = document.getElementById('list');
    const open = document.getElementById('openBtn')
    open.addEventListener('click',()=>{
      listLayout.style.display = "block"
    })

      const response = await fetch("https://reqres.in/api/users?page=1");
      const jsonresponse = await response.json();
      setUser(jsonresponse.data);
  };

  // useEffect(() => {
  //   setUser();
  // }, []);

  // if(loading){
  //   return <h1>Loading</h1>
  // }

  return (
    <>
      <Nav loadUser={loadUser}  />
      <div className="app">


        <h2>User:</h2>

        <ul className="layout" id="list">
          {user.map((item) => (
            <li>
              <div className="profile">
                <img src={item.avatar}></img>
              </div>
              <div className="innerDetl">
                <p className="name">Name : {item.first_name} {item.last_name}</p>
                <p className="email">Email : {item.email}</p>
                <p className="phone">ID : {item.id}</p>
              </div>
            </li>
          ))}
        </ul> 

      </div>
    </>
  );
};
export default App;
