import React, { useState,useEffect } from "react";
import "./App.css";


const App = () => {
  const [user, setUser] = useState([]);

  const [isclose , setIsClose] = useState(true)


const btns = document.getElementById('closeBtn') 
const listLayout = document.getElementById('list')
const closebtn = () => {
  btns.addEventListener('click',() => {
  listLayout.style.display = "none"

})
}



  const loadUser = async () => {
    const listLayout = document.getElementById('list');
    const open = document.getElementById('openBtn')
    open.addEventListener('click',()=>{
      listLayout.style.display = "grid"
    })

      const response = await fetch("https://reqres.in/api/users?page=1");
      const jsonresponse = await response.json();
      setUser(jsonresponse.data);
  };

  // useEffect(() => {
  //   setUser();
  // }, []);

  // if(loading){
  //   return <h1>Loading</h1>
  // }

  return (
    <>
        <nav>
        <div className="flex">
            <h1>FetchingData</h1>  
            <button onClick={loadUser} class="btn " id="openBtn">Get User</button> 
            <button onClick={closebtn} class="btn-close " id="closeBtn"></button> 
        </div>
      </nav>

      <div className="app">


        <h2>User:</h2>

        <ul className="layout" id="list">
          {user.map((item) => (
            <li>
              <div className="profile">
                <img src={item.avatar}></img>
              </div>
              <div className="innerDetl">
                <p className="name">Name : {item.first_name} {item.last_name}</p>
                <p className="email">Email : {item.email}</p>
                <p className="phone">ID : {item.id}</p>
              </div>
            </li>
          ))}
        </ul> 

      </div>
    </>
  );
};
export default App;
