import React, { useState } from "react";
import "./App.css";
import img from "../src/img-1.jpg";
import Nav from "./Components/Nav";

const App = () => {
  const [user, setUser] = useState([]);



  const loadUser = async () => {
    const response = await fetch("https://reqres.in/api/users?page=1");
    const jsonresponse = await response.json();
    setUser(jsonresponse.data);

  };



  // useEffect(() => {
  //   loadUser();
  // }, []);

  return (
    <>
      <Nav loadUser={loadUser} closeBtn ={closeUser} />
      <div className="app">


        <h2>User:</h2>

        <ul className="layout">
          {user.map((item) => (
            <li>
              <div className="profile">
                <img src={item.avatar}></img>
              </div>
              <div className="innerDetl">
                <p className="name">Name : {item.first_name} {item.last_name}</p>
                <p className="email">Email : {item.email}</p>
                <p className="phone">ID : {item.id}</p>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};
export default App;
